import React, { Component } from "react";
import "../Component/MainVideo/MainVideo.scss";
import { Link } from "react-router-dom";
import axios from "axios";

class AddNotes extends Component {
  state = {
    note: "",
  };

  handleChange = (event) => {
    this.setState({
      [event.target.input]: event.target.value,
    });
    console.log(event.target.input);
  };

  handleSubmit = (event) => {
    event.preventDefault();
    if (!this.isFormValid()) {
      axios.post(`${NEW_URL}`, this.state).then((addVideo) => {
        // console.log(addVideo);
        // console.log("id", addVideo.data.id);
        axios
          .post(`${NEW_URL}/${addVideo.data.id}`, this.state)
          .then((addVideoComments) => {
            //console.log(addVideoComments);
            this.setState({
              title: "",
              description: "",
            });
            this.props.history.push("/");
          });
      });
      alert("Upload successfully");
    } else {
      alert("Field cannot be blank, please enter a title and description");
    }
  };

  render() {
    return (
      <div>
        <textarea
          className="main__notebox"
          input="text"
          placeholder="Make Notes here..."
          onChange={handleChange}
        />

        <div className="main__button">
          <button className="main__button__save">Save</button>
          <Link className="main__button__link" to="/notes">
            <button className="main__button__save">Notes</button>
          </Link>
        </div>
      </div>
    );
  }
}

export default AddNotes;
